from __future__ import print_function

import sys, os

sys.path.insert(0, os.path.normpath(
    os.path.abspath(os.path.dirname(sys.argv[0]))+'/../'
))
import gtk
import vineyard
import wine

class Main():
    def __init__(self):
        self.widgets = vineyard.LoadWidgets(
            self,
            filename=os.path.join(
                os.path.abspath(os.path.dirname(sys.argv[0])+'/../'),
                'data/vineyard-programs.glade'
            )
        )
        self.widgets['window'].show_all()

        self.widgets['window'].connect('destroy',
                                       lambda *args: gtk.main_quit()
        )

        self.slist_configured = vineyard.SimpleList(
            headers = ['', "Name"],
            types = [str, gtk.gdk.Pixbuf, str]
        )
        self.widgets['notebook'].append_page(
            self.slist_configured, gtk.Label("Configured")
        )
        self.slist_configured.connect('changed', self.on_changed)

        self.slist_others = vineyard.SimpleList(
            headers = ['', "Name", "Type"],
            types = [str, gtk.gdk.Pixbuf, str, str]
        )
        self.widgets['notebook'].append_page(
            self.slist_others, gtk.Label("Others")

        )
        self.slist_others.connect('changed', self.on_changed)

        self.widgets['window'].set_size_request(480, 350)

        self.get_programs_list()

        gtk.main()


    def on_changed(self, simplelist, row, column_nr, content):
        print(simplelist, row, column_nr, content)

    def get_programs_list(self):
        # Separate the list of programs into types
        configured = []
        programs = []
        libraries = []
        websites = []
        for program in self.get_from_registry():
            if program['parsed from'] == 'setting':
                configured.append(program)
            elif 'is_url' in program:
                websites.append(program)
            elif 'programexe' in program or 'programcommand' in program:
                programs.append(program)
            else:
                libraries.append(program)

        # Add the configured programs to slist_configured
        for program in sorted(configured, key=lambda i: i['name'].lower()):
            self.slist_configured.model.append([
                program['_registrykey'],
                vineyard.icons.get_icon_pixbuf_from_program(program),
                program['name']
            ])

        # Add the rest to slist_others, in order of types
        for _programs, _type in (
            (
                sorted(programs, key=lambda i: i['name'].lower()),
                "Program"
            ),
            (
                sorted(libraries, key=lambda i: i['name'].lower()),
                "Library"
            ),
            (
                sorted(websites, key=lambda i: i['name'].lower()),
                "Website"
            )
        ):
            for program in _programs:
                self.slist_others.model.append([
                    program['_registrykey'],
                    vineyard.icons.get_icon_pixbuf_from_program(program),
                    program['name'],
                    _type
                ])

    def get_from_registry(self):
        return iter(PROGRAMS_TEMP)

    def get_from_menu(self):
        return iter(PROGRAMS_TEMP)

